import * as React from 'react';
import * as ReactDOM from 'react-dom';
import { Form, FormElement } from '@progress/kendo-react-form';
import { Button } from '@progress/kendo-react-buttons';
import { Stepper } from '@progress/kendo-react-layout';
import { NameAndRegion } from './CreateVps/NameAndRegion';
import { BasicConfig } from './CreateVps/BasicConfig';
import { Networking } from './CreateVps/Networking';
const stepPages = [NameAndRegion, BasicConfig, Networking];
const CreateVpsForm = () => {
  const [step, setStep] = React.useState(0);
  const [formState, setFormState] = React.useState({});
  const [steps, setSteps] = React.useState([{
    label: 'Name and Region',
    isValid: undefined
  }, {
    label: 'Basic Config',
    isValid: undefined
  }, {
    label: 'Networking',
    isValid: undefined
  }]);
  const lastStepIndex = steps.length - 1;
  const isLastStep = lastStepIndex === step;
  const onStepSubmit = React.useCallback(event => {
    const {
      isValid,
      values
    } = event;
    const currentSteps = steps.map((currentStep, index) => ({
      ...currentStep,
      isValid: index === step ? isValid : currentStep.isValid
    }));
    setSteps(currentSteps);
    if (!isValid) {
      return;
    }
    setStep(() => Math.min(step + 1, lastStepIndex));
    setFormState(values);
    if (isLastStep) {
      alert(JSON.stringify(values));
    }
  }, [steps, isLastStep, step, lastStepIndex]);
  const onPrevClick = React.useCallback(event => {
    event.preventDefault();
    setStep(() => Math.max(step - 1, 0));
  }, [step, setStep]);
  return <div style={{
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center'
  }}>
        <Stepper value={step} items={steps} />
        <Form initialValues={formState} onSubmitClick={onStepSubmit} render={formRenderProps => <div style={{
      alignSelf: 'center'
    }}>
              <FormElement style={{
        width: 480
      }}>
                {stepPages[step]}
                <span style={{
          marginTop: '40px'
        }} className={'k-form-separator'} />
                <div style={{
          justifyContent: 'space-between',
          alignContent: 'center'
        }} className={'k-form-buttons k-button k-button-md k-rounded-md k-button-solid k-button-solid-bases-end'}>
                  <span style={{
            alignSelf: 'center'
          }}>Step {step + 1} of 3</span>
                  <div>
                    {step !== 0 ? <Button style={{
              marginRight: '16px'
            }} onClick={onPrevClick}>
                                            Previous
                                          </Button> : undefined}
                    <Button themeColor={'primary'} disabled={!formRenderProps.allowSubmit} onClick={formRenderProps.onSubmit}>
                      {isLastStep ? 'Submit' : 'Next'}
                    </Button>
                  </div>
                </div>
              </FormElement>
            </div>} />
      </div>;
};
export default CreateVpsForm
