import React, { useState, useEffect } from "react";

import UserService from "../services/user.service";
import RecentTaskEntry from "./RecentTaskEntry";

const RecentTasks = () => {
  const [content, setContent] = useState("");

  useEffect(() => {
    UserService.listRecentTasks().then(
      (response) => {
        setContent(response.data);
      },
      (error) => {
        const _content =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setContent(_content);
      }
    );
  }, []);

  return (
    <div className="container-fluid">
            <div className="row">
                <div className="col-lg-7 col-xl-12">
                    <div className="card shadow mb-4">
                        <div className="card-header d-flex justify-content-between align-items-center">
                            <h6 className="text-primary fw-bold m-0">Recent tasks</h6>
                            <div className="dropdown no-arrow"><button className="btn btn-link btn-sm dropdown-toggle" aria-expanded="false" data-bs-toggle="dropdown" type="button"><i className="fas fa-ellipsis-v text-gray-400"></i></button>
                                <div className="dropdown-menu shadow dropdown-menu-end animated--fade-in">
                                    <p className="text-center dropdown-header">Manage tasks</p><a className="dropdown-item" href="#">Clear</a><a className="dropdown-item" href="#">Export</a>
                                    <div className="dropdown-divider"></div><a className="dropdown-item" href="#">Notification Settings</a>
                                </div>
                            </div>
                        </div>
                        <div className="card-body">
                            <div className="table-responsive">
                                <table className="table">
                                    <thead>
                                        <tr>
                                            <th>Action</th>
                                            <th>Time</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {content.map((user) => (
                                          <RecentTaskEntry
                                            name={`${user.name.first} ${user.name.last}`}
                                            avatar={user.picture.thumbnail}
                                            email={user.email}
                                            key={user.id.value}
                                          />
                                        ))}
                                        <tr>
                                            <td>Cell 3</td>
                                            <td>Cell 4</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
  );
};

export default RecentTasks;
