import React, { useState, useEffect } from "react";
import { mockComponent } from "react-dom/test-utils";
import Dropdown from "react-bootstrap";

import UserService from "../services/user.service";
import RecentTaskEntry from "./RecentTaskEntry";

const RecentTasks = () => {
  const [content, setContent] = useState([]);


  useEffect(() => {
    UserService.listRecentTasks().then(
      (response) => {
        setContent(response.data);
      },
      (error) => {
        const _content =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setContent(_content);
      }
    );
  }, []);
  return (
    <div className="container-fluid">
            <div className="row">
                <div className="col-lg-7 col-xl-12">
                    <div className="card shadow mb-4">
                        <div className="card-header d-flex justify-content-between align-items-center">
                            <h6 className="text-primary fw-bold m-0">Recent tasks</h6>
                            <Dropdown>
                              <Dropdown.Toggle className="btn btn-link btn-sm dropdown-toggle">
                              </Dropdown.Toggle>

                              <Dropdown.Menu className="dropdown-menu shadow dropdown-menu-end animated--fade-in">
                                <Dropdown.Header className="text-center">Manage recent tasks</Dropdown.Header>
                                <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                              </Dropdown.Menu>
                            </Dropdown>
                            <Dropdown
                                title={
                                    <span>
                                        <i className='fas fa-ellipsis-v text-gray-400'></i> Dropdown
                                    </span>
                                }
                                id='collasible-nav-dropdown'>
                                <NavDropdown.Item href='#action/3.1'>Action 1</NavDropdown.Item>
                                <NavDropdown.Item href='#action/3.3'>Action 2</NavDropdown.Item>
                            </Dropdown>
                            <div className="dropdown no-arrow"><button className="e" aria-expanded="false" data-bs-toggle="dropdown" type="button"><i className=""></i></button>
                                <div >
                                    <p >Manage tasks</p><a className="dropdown-item" href="#">Clear</a><a className="dropdown-item" href="#">Export</a>
                                    <div className="dropdown-divider"></div><a className="dropdown-item" href="#">Notification Settings</a>
                                </div>
                            </div>
                        </div>
                        <div className="card-body">
                            <div className="table-responsive">
                                <table className="table">
                                    {(() => {
                                            if (content){
                                                return (
                                                  <thead>
                                                    <tr>
                                                      <th>Action</th>
                                                      <th>Time</th>
                                                    </tr>
                                                  </thead>
                                                ) 
                                            } else {
                                              return null
                                            }
                                    })()}
                                    <tbody>
                                          {(() => {
                                            if (content){
                                                return (
                                                  content.map ((recent_tasks => 
                                                  <RecentTaskEntry message={recent_tasks.message} createdAt={recent_tasks.createdAt}/>
                                                )))  
                                            } else {
                                              return (
                                              <div>
                                                <br/>
                                                <h6 className="text-primary fw-bold m-0">No recent tasks found.</h6>
                                              </div>
                                              )
                                            }
                                          })()}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
  );
};

export default RecentTasks;
