import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import Form from 'react-bootstrap/Form'
import Input from "react-validation/build/input";
import { addRegion } from "../actions/admin";
import CheckButton from "react-validation/build/button";

const required = (value) => {
  if (!value) {
    return (
      <div role="alert" style={{"marginTop":"5px","color":"red"}}>
        This field is required!
      </div>
    );
  }
};

const CreateRegionForm = (props) => {
  

  return (
    <Form className="user" onSubmit={handleCreateRegion} ref={form} style={{margin: '18px'}}>
      <div className="mb-3">
          <Input
          id="InputFullName"
          type="text"
          className="form-control  form-control-user"
          placeholder="Full Name"
          name="fullName"
          value={fullName}
          onChange={onChangeFullName}
          validations={[required]}
          />
      </div>
      <div className="mb-3">
          <Input
          id="InputShortName"
          type="text"
          className="form-control form-control-user"
          name="shortName"
          placeholder="Short Name"
          value={shortName}
          onChange={onChangeShortName}
          validations={[required]}
          />
      </div>
      <div className="mb-3">
          <Input
          id="InputProxHost"
          type="text"
          className="form-control form-control-user"
          name="proxHost"
          placeholder="Proxmox IP or Hostname"
          value={proxHost}
          onChange={onChangeProxHost}
          validations={[required]}
          />
      </div>
      <div className="mb-3">
          <Input
          id="InputProxPort"
          type="number"
          className="form-control form-control-user"
          name="proxPort"
          placeholder="Promox API Port"
          value={proxPort }
          onChange={onChangeProxPort}
          validations={[required]}
          />
      </div>
      <div className="mb-3">
          <Input
          id="InputProxUser"
          type="text"
          className="form-control form-control-user"
          name="proxUser"
          placeholder="Proxmox API User"
          value={proxUser}
          onChange={onChangeProxUser}
          validations={[required]}
          />
      </div>
      <div className="mb-3">
          <Input
          id="InputPassword"
          type="password"
          className="form-control form-control-user"
          name="password"
          placeholder="Password"
          value={password}
          onChange={onChangePassword}
          validations={[required]}
          />
      </div>
      <div className="mb-3">
      </div><button className="btn btn-primary d-block btn-user w-100" type="submit" disabled={loading}>Create Region
      {loading && (
          <span className="spinner-border spinner-border-sm"></span>
      )}
      </button>
      <hr></hr>
      {message && (
      <div className="form-group">
            <div className="alert alert-danger" role="alert">
                {message}
            </div>
      </div>
      )}
      <CheckButton style={{ display: "none" }} ref={checkBtn} />
    </Form>
  )
}

export default CreateRegionForm
