import React, { useState, useEffect } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";
import UserService from "../services/user.service";


const ChangePassword = () => {

    const onChangePassword = (e) => {
        const password = e.target.value;
        setPassword(password);
      };

    const onChangePassword2 = (e) => {
    const password2 = e.target.value;
    setPassword2(password2);
    };

    const [password, setPassword] = useState("");
    const [password2, setPassword2] = useState("");
    const [loading, setLoading] = useState(false);


    return (
        <div className="card shadow mb-4">
            <div className="card-header d-flex justify-content-between align-items-center">
                <h6 className="text-primary fw-bold m-0">Password</h6>
            </div>
            <div className="card-body">
                <div className="row">
                    <div className="col-sm">
                        <div className="table-responsive">
                            <table className="table">
                                <tbody>
                                {loading && (
                                    <span className="spinner-border spinner-border-sm"></span>
                                )}
                                    <td><span style={{fontWeight: 'bold'}}>Old password</span></td>
                                    <td><Input
                                            id="exampleInputPassword"
                                            type="password"
                                            className="form-control form-control-user"
                                            name="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={onChangePassword}
                                            validations={[required, vpassword]}
                                        />
                                    </td>
                                    <tr></tr>
                                    <td><span style={{fontWeight: 'bold'}}>New password</span></td>
                                    <td><Input
                                            id="exampleInputPassword"
                                            type="password"
                                            className="form-control form-control-user"
                                            name="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={onChangePassword}
                                            validations={[required, vpassword]}
                                        />     
                                    </td>
                                    <tr></tr>
                                    <td><span style={{fontWeight: 'bold'}}>Repeat new password</span></td>
                                    <td><Input
                                        id="exampleInputPassword"
                                        type="password"
                                        className="form-control form-control-user"
                                        name="password"
                                        value={password2}
                                        placeholder="Repeat password"
                                        onChange={onChangePassword2}
                                        validations={[required, vpassword, matchpassword]}
                                        />
                                    </td>
                                </tbody>
                            </table>
                        </div>
                        <button class="btn btn-primary" type="button"

                            disabled={loading}
                        >Change password
                        </button>                        
                    </div>
                    <div className="col-sm"/>
                </div>
            </div>
        </div>
    )
}

export default ChangePassword
