import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from 'react-router-dom';

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";

import logo from './../assets/img/logos/logo-white-small.png'
import illustration from './../assets/img/illustrations/server1.png'

import { login } from "../actions/auth";

const required = (value) => {
  if (!value) {
    return (
      <div classNameName="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const Login = (props) => {
  const form = useRef();
  const checkBtn = useRef();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const { isLoggedIn } = useSelector(state => state.auth);
  const { message } = useSelector(state => state.message);

  const dispatch = useDispatch();

  const onChangeEmail = (e) => {
    const email = e.target.value;
    setEmail(email);
  };

  const onChangePassword = (e) => {
    const password = e.target.value;
    setPassword(password);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    setLoading(true);

    form.current.validateAll();

    if (checkBtn.current.context._errors.length === 0) {
      dispatch(login(email, password))
        .then(() => {
          props.history.push("/dashboard");
          window.location.reload();
        })
        .catch(() => {
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  };

  if (isLoggedIn) {
    return <Redirect to="/dashboard" />;
  }

  return (
        <body className="bg-gradient-primary">
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-9 col-lg-12 col-xl-10">
                        <div className="card shadow-lg o-hidden border-0 my-5">
                            <div className="card-body p-0">
                                <div className="row">
                                    <div className="col-lg-6 col-xl-6 d-none d-lg-flex">
                                        <div className="flex-grow-1 bg-login-image" style={{'background-size': 'contain','background-image': ''}}></div>
                                    </div>
                                    <div className="col-lg-6 col-xl-6">
                                        <div className="p-5">
                                            <div className="text-center">
                                                <h4 className="text-dark mb-4">Welcome Back!</h4>
                                            </div>
                                            <Form className="user" onSubmit={handleLogin} ref={form} style={{margin: '19px'}}>
                                                <div className="mb-3">
                                                    <label htmlFor="email">Email</label>
                                                    <Input
                                                    type="text"
                                                    classNameName="form-control"
                                                    name="email"
                                                    value={email}
                                                    onChange={onChangeEmail}
                                                    validations={[required]}
                                                    />
                                                </div>
                                                <div className="mb-3">
                                                    <label htmlFor="password">Password</label>
                                                    <Input
                                                    type="password"
                                                    classNameName="form-control"
                                                    name="password"
                                                    value={password}
                                                    onChange={onChangePassword}
                                                    validations={[required]}
                                                    />
                                                </div>
                                                <div className="mb-3">
                                                </div><button className="btn btn-primary d-block btn-user w-100" type="submit" disabled={loading}>Login
                                                {loading && (
                                                    <span classNameName="spinner-border spinner-border-sm"></span>
                                                )}
                                                </button>

                                                {message && (
                                                <div className="form-group">
                                                <div className="alert alert-danger" role="alert">
                                                    {message}
                                                </div>
                                                </div>
                                            )}
                                            <CheckButton style={{ display: "none" }} ref={checkBtn} />
                                            </Form>
                                            <div className="text-center"><a className="small" href="forgot-password.html">Forgot Password?</a></div>
                                            <div className="text-center"><a className="small" href="register.html">Create an Account!</a></div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="d-xl-flex mx-auto justify-content-xl-center sidebar-brand-text mx-3"><br>
            </br><img className="d-xl-flex mx-auto justify-content-xl-center" src="logo" alt="unavailable"></img><br>
            </br></div>
            <script src="assets/bootstrap/js/bootstrap.min.js"></script>
            <script src="https://cdnjs.cloudflare.com/ajax/libs/Swiper/6.4.8/swiper-bundle.min.js"></script>
        </body>    
  );
};

export default Login;
