import * as React from 'react';
import { Field } from '@progress/kendo-react-form';
import { FormInput, FormUpload } from './FormComponents';
import { useState, useEffect } from "react";
import { userNameValidator, emailValidator, passwordValidator, FormRadioGroup, vpsNameValidator } from './Validators';
import { RadioGroup } from '@progress/kendo-react-inputs';
import vpsService from '../../../services/vps.service';
export const NameAndRegion = () => {
    const [regions, setRegions] = useState([]);
    const [loading, setLoading] = useState(false);
    const [reload, setReload] = useState(false);
  
    
  
    useEffect(() => {
      setLoading(true);
      vpsService.listRegionsUser().then(
        (response) => {
          console.log(response.data)
          setRegions(response.data)
          setLoading(false);
          setReload(false)
        },
        (error) => {
          const _content =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
  
          setRegions(_content);
        }
      );
    }, [reload]);

       
  return (
    <div>
        <Field key={'vpsName'} id={'vpsName'} name={'vpsName'} label={'Name of the VPS'} component={FormInput} validator={vpsNameValidator} />
        <Field key={'region'} id={'region'} name={'region'} label={'Heycloud Region'} data={this.state.regions.map(({ regions }) => {return { value: regions.shortName, label: regions.fullName }})} component={FormRadioGroup} />
    </div>
  )

}
