import React, { useState, useEffect } from "react";
import NavbarSide from '../components/NavBarSide';
import NavbarTop from "../components/NavBarTop"
import Footer from '../components/Footer';
import RecentTasks from '../components/RecentTasks';
import DashboardWidgeds from '../components/DashboardWidgeds';
import { useSelector } from "react-redux";
import { Redirect } from 'react-router-dom';
import GifLoader from 'react-gif-loader';
import Preloader from '../assets/img/logos/logo-preloader.gif'
import querySearch from "stringquery";



const Dashboard = (props) => {
    const { user: currentUser } = useSelector((state) => state.auth);
    const [preloaderState, setPreloaderStat] = useState(false);



    useEffect(() => {
        if (querySearch(props.location.search).preloader) {
            setPreloaderStat(true)

          } else {
            setPreloaderStat(false)
        }
    }, []);

    const resetPreloaderStat = () => {
        setPreloaderStat(false)
    };

    if (!currentUser) {
      return <Redirect to="/login" />;
    }
    return (
        <div id="wrapper">
            <GifLoader
                loading={preloaderState}
                imageSrc={Preloader}
                imageStyle={{height: '50%',' margin-left': 'auto','margin-right': 'auto', 'margin-top': '10%'}}
                overlayBackground="rgba(255,255,255,255)"
            />
            <NavbarSide/>
            <div class="d-flex flex-column" id="content-wrapper">
                <div id="content">
                <NavbarTop user={currentUser.user}/>
                    <div class="container-fluid">
                        <div class="d-sm-flex justify-content-between align-items-center mb-4">
                            <h3 class="text-dark mb-0">Dashboard</h3>
                        </div>
                        <DashboardWidgeds />
                        <RecentTasks />
                    </div>
                </div>
                <Footer />
            </div>
        </div>
    )
}

export default Dashboard
