import React, { useState, useEffect } from "react";

import UserService from "../services/user.service";

useEffect(() => {
    const [content, setContent] = useState([]);
    const [loading, setLoading] = useState(false);
    const [reload, setReload] = useState(false);
    setLoading(true);
    UserService.listRecentTasks().then(
      (response) => {
        setContent(response.data);
        setLoading(false);
        setReload(false)
      },
      (error) => {
        const _content =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setContent(_content);
      }
    );
  }, [reload]);

const UserInfo = (props) => {
    return (
        <div className="row">
        <div className="col-lg-7 col-xl-12">
            <div className="card shadow mb-4">
                <div className="card-header d-flex justify-content-between align-items-center">
                    <h6 className="text-primary fw-bold m-0">Customer information</h6>
                </div>
                <div className="card-body">
                    <div className="row">
                        <div className="col-sm">
                            <div className="table-responsive">
                                <table className="table">
                                    <tbody>
                                    {loading && (
                                      <span className="spinner-border spinner-border-sm"></span>
                                    )}
                                        <td><span style={{fontWeight: 'bold'}}>Name</span></td>
                                        <td>{props.user.prename + " " + props.user.name}</td>
                                        <tr></tr>
                                        <td><span style={{fontWeight: 'bold'}}>Email</span></td>
                                        <td>{props.user.email}</td>
                                        <tr></tr>
                                        <td><span style={{fontWeight: 'bold'}}>Phone</span></td>
                                        <td>{props.user.phone}</td>

                                    </tbody>
                                </table>
                            </div>
                        </div>
                        <div className="col-sm"/>
                        <div className="col-sm"/>
                    </div>
                </div>
            </div>
        </div>
    </div>
    )
}

export default UserInfo
